import React from "react";

export default function PlayStation() {
  const accounts = [
    {
      title: "ÿ≠ÿ≥ÿßÿ® PlayStation üá¨üáß",
      image: "/images/playstation/playstation1.jpg",
      desc: [
        "ÿßŸÑÿ≠ÿ≥ÿßÿ® ÿ≥ŸàŸÜŸä ŸàÿßŸÑÿ≥ÿ™Ÿàÿ± ÿ®ÿ±Ÿäÿ∑ÿßŸÜŸä (GB)",
        "Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿ£ŸÉÿ´ÿ± ŸÖŸÜ 180 ŸÑÿπÿ®ÿ© ÿ™ÿÆÿ™ŸäŸÖ Ÿàÿ£ŸàŸÜŸÑÿßŸäŸÜ üî•",
        "ÿ£ŸáŸÖ ÿßŸÑÿ£ŸÑÿπÿßÿ®: Uncharted 4 ‚Äì RDR2 ‚Äì The Last of Us (ŸÉŸÑ ÿßŸÑŸÜÿ≥ÿÆ) ‚Äì God of War ‚Äì Resident Evil (ŸÉŸÑ ÿßŸÑÿ£ÿ¨ÿ≤ÿßÿ°) ‚Äì Spider-Man ‚Äì Outlast",
      ],
      price: "500 ÿ±ŸäÿßŸÑ",
      images: Array.from({ length: 18 }, (_, i) => `/images/playstation/playstation${i + 1}.jpg`),
    },
  ];

  return (
    <div className="bg-black text-white min-h-screen py-10 px-4">
      <h1 className="text-4xl font-bold text-center text-yellow-400 mb-10">
        üéÆ ÿ≠ÿ≥ÿßÿ®ÿßÿ™ PlayStation ÿßŸÑŸÖÿ™ŸàŸÅÿ±ÿ©
      </h1>

      <div className="max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-8">
        {accounts.map((acc, idx) => (
          <div
            key={idx}
            className="bg-gradient-to-b from-[#1f1f2e] to-[#111] rounded-3xl shadow-xl p-5 hover:scale-[1.02] transition-all border border-gray-800"
          >
            <img
              src={acc.image}
              alt={acc.title}
              className="rounded-2xl w-full h-56 object-cover mb-4 shadow-md"
            />

            <h2 className="text-2xl font-bold text-yellow-300 mb-2 text-center">
              {acc.title}
            </h2>

            <ul className="text-gray-300 text-sm space-y-2 leading-relaxed px-2">
              {acc.desc.map((line, i) => (
                <li key={i}>üîπ {line}</li>
              ))}
            </ul>

            <p className="text-green-400 text-lg font-semibold text-center mt-4">
              ÿßŸÑÿ≥ÿπÿ±: {acc.price}
            </p>

            <div className="flex justify-center gap-3 mt-5">
              <button className="bg-green-500 hover:bg-green-600 text-white font-semibold py-2 px-4 rounded-xl">
                ÿ™ŸàÿßÿµŸÑ Ÿàÿßÿ™ÿ≥ÿßÿ®
              </button>
              <button
                onClick={() => {
                  const gallery = document.getElementById(`gallery-${idx}`);
                  gallery.scrollIntoView({ behavior: "smooth" });
                }}
                className="bg-gray-700 hover:bg-gray-600 text-white font-semibold py-2 px-4 rounded-xl"
              >
                ÿπÿ±ÿ∂ ÿßŸÑÿµŸàÿ±
              </button>
            </div>
          </div>
        ))}
      </div>

      {accounts.map((acc, idx) => (
        <div key={idx} id={`gallery-${idx}`} className="max-w-6xl mx-auto mt-14">
          <h3 className="text-3xl font-bold text-center text-yellow-400 mb-6">
            üì∏ ŸÖÿπÿ±ÿ∂ ÿµŸàÿ± ÿßŸÑÿ≠ÿ≥ÿßÿ®
          </h3>

          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
            {acc.images.map((src, i) => (
              <img
                key={i}
                src={src}
                alt={`PlayStation Account ${i + 1}`}
                className="rounded-xl shadow-md hover:scale-[1.03] transition-transform"
              />
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
