import React, { useState } from "react";
import { ShoppingCart, LogIn, Phone, Star } from "lucide-react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

function Home() {
  const [cart, setCart] = useState([]);
  const [loggedIn, setLoggedIn] = useState(false);

  const sections = [
    { name: "ÿ≠ÿ≥ÿßÿ®ÿßÿ™ ŸÇŸÜÿ¥ŸÜ", color: "from-purple-700 to-indigo-600", image: "/assets/genshin.png" },
    { name: "ÿ≠ÿ≥ÿßÿ®ÿßÿ™ ŸÅŸàÿ±ÿ™ŸÜÿßŸäÿ™", color: "from-blue-500 to-cyan-400", image: "/assets/fortnite.png" },
    { name: "ÿ≠ÿ≥ÿßÿ®ÿßÿ™ ŸÅŸäŸÅÿß", color: "from-green-600 to-lime-500", image: "/assets/fifa.png" },
    { name: "ÿ≠ÿ≥ÿßÿ®ÿßÿ™ ÿ≥ŸàŸÜŸä", color: "from-gray-700 to-blue-900", image: "/assets/ps.png" },
    { name: "ÿ≠ÿ≥ÿßÿ®ÿßÿ™ ÿ±Ÿàÿ®ŸÑŸàŸÉÿ≥", color: "from-red-600 to-yellow-500", image: "/assets/roblox.png" },
    { name: "ÿ≠ÿ≥ÿßÿ®ÿßÿ™ ÿ£ŸàŸÅÿ± Ÿàÿßÿ™ÿ¥", color: "from-orange-600 to-amber-500", image: "/assets/overwatch.png" }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-black to-gray-900 text-white font-sans">
      <header className="flex items-center justify-between px-6 py-4 border-b border-gray-700 bg-black/50 backdrop-blur-md sticky top-0 z-50">
        <h1 className="text-3xl font-extrabold text-amber-400">
          <Link to="/">AMH STORE</Link>
        </h1>
        <nav className="flex items-center gap-4 text-sm">
          <Link to="/faq" className="hover:text-amber-300">ÿßŸÑÿ£ÿ≥ÿ¶ŸÑÿ© ÿßŸÑÿ¥ÿßÿ¶ÿπÿ©</Link>
          <Link to="/privacy" className="hover:text-amber-300">ÿßŸÑÿÆÿµŸàÿµŸäÿ©</Link>
          <Link to="/terms" className="hover:text-amber-300">ÿßŸÑÿ¥ÿ±Ÿàÿ∑</Link>
        </nav>
        <div className="flex items-center gap-4">
          <button
            onClick={() => setLoggedIn(!loggedIn)}
