    userCart.push(item);
    localStorage.setItem(userCartKey, JSON.stringify(userCart));
    alert("ÿ™ŸÖÿ™ ÿßŸÑÿ•ÿ∂ÿßŸÅÿ© ÿ•ŸÑŸâ ÿßŸÑÿ≥ŸÑÿ© ‚úÖ");
  };

  return (
    <div className="bg-black text-white min-h-screen py-10 text-center">
      <h1 className="text-3xl font-bold text-amber-400 mb-8">ÿ≠ÿ≥ÿßÿ®ÿßÿ™ ŸÅŸàÿ±ÿ™ŸÜÿßŸäÿ™</h1>

