import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();



  const handleLogin = () => {
    if (!email || !password) {
      setMessage("الرجاء إدخال جميع الحقول.");
      return;
    }

    let users = JSON.parse(localStorage.getItem("users")) || [];

    const existingUser = users.find((u) => u.email === email);

    if (existingUser) {
      // تسجيل دخول
      if (existingUser.password === password) {
        localStorage.setItem("loggedUser", JSON.stringify(existingUser));
        setMessage("✅ تم تسجيل الدخول بنجاح");
        setTimeout(() => navigate("/"), 1000);
      } else {
        setMessage("❌ كلمة المرور غير صحيحة");
      }
    } else {
      // إنشاء حساب جديد
      const newUser = { email, password };
      users.push(newUser);
      localStorage.setItem("users", JSON.stringify(users));
      localStorage.setItem("loggedUser", JSON.stringify(newUser));
      setMessage("🎉 تم إنشاء الحساب وتسجيل الدخول بنجاح");
      setTimeout(() => navigate("/"), 1000);
    }
  };

  return (
    <div className="bg-black text-white min-h-screen flex flex-col justify-center items-center">
      <h1 className="text-3xl font-bold text-amber-400 mb-6">تسجيل الدخول</h1>
      <div className="bg-gray-900 p-6 rounded-xl w-80 shadow-lg">
        <input
          type="email"
          placeholder="البريد الإلكتروني"
          className="w-full p-2 mb-3 rounded text-black"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="كلمة المرور"
          className="w-full p-2 mb-4 rounded text-black"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button
          onClick={handleLogin}
          className="w-full bg-amber-400 hover:bg-amber-300 text-black font-bold py-2 rounded"
        >
          تسجيل الدخول / إنشاء حساب
        </button>
        {message && <p className="mt-3 text-sm text-gray-300">{message}</p>}
      </div>
    </div>
  );
}
