import React from "react";
import { Link } from "react-router-dom";

export default function FAQ() {
  const faqs = [
    { q: "ماهو متجر AMH؟", a: "متجر متخصص في بيع الحسابات الرقمية المضمونة 100%." },
    { q: "هل نحن مضمونون؟", a: "نعم، جميع الحسابات مصدرها موثوق ومضمون." },
    { q: "هل نعرض حسابات الناس للبيع؟", a: "نعم، بشرط أن تكون الحسابات غير مربوطة ومصدرها مؤكد." },
    { q: "ما طرق الدفع؟", a: "نوفر Apple Pay و STC Pay والتحويل البنكي." },
  ];

  return (
    <div className="bg-black text-white min-h-screen p-10 text-center">
      <h1 className="text-3xl font-bold text-amber-400 mb-6">الأسئلة الشائعة ❓</h1>
      <div className="w-full sm:w-2/3 mx-auto">
        {faqs.map((item, i) => (
          <details key={i} className="bg-gray-900 p-4 rounded-xl mb-3 text-right">
            <summary className="cursor-pointer text-amber-300 text-lg font-semibold">{item.q}</summary>
            <p className="mt-2 text-gray-400">{item.a}</p>
          </details>
        ))}
      </div>
      <Link to="/" className="text-blue-400 hover:text-amber-300">⬅ العودة إلى الصفحة الرئيسية</Link>
    </div>
  );
}
