import React, { useState, useEffect } from "react";
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

import Genshin from "./pages/Genshin";
import Fifa from "./pages/Fifa";
import Fortnite from "./pages/Fortnite";
import Roblox from "./pages/Roblox";
import Playstation from "./pages/Playstation";
import Overwatch from "./pages/Overwatch";
import Cart from "./pages/Cart";
import PrivacyPolicy from "./pages/PrivacyPolicy";
import TermsConditions from "./pages/TermsConditions";
import FAQ from "./pages/FAQ";
import Login from "./pages/Login";

function App() {
  // ✅ حالة السلة
  const [cart, setCart] = useState(() => {
    const saved = localStorage.getItem("cart");
    return saved ? JSON.parse(saved) : [];
  });

  // ✅ حفظ السلة في LocalStorage
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  // ✅ حالة تسجيل الدخول (مبدئية)
  const [loggedIn, setLoggedIn] = useState(() => {
    return localStorage.getItem("loggedIn") === "true";
  });

  useEffect(() => {
    localStorage.setItem("loggedIn", loggedIn);
  }, [loggedIn]);

  return (
    <BrowserRouter>
      <div className="bg-black min-h-screen text-white">
        {/* ✅ رأس الصفحة */}
        <header className="bg-gradient-to-r from-orange-800 to-yellow-500 p-4 flex justify-between items-center text-black">
          <Link to="/" className="text-xl font-bold text-black">
            AMH STORE
          </Link>

          <nav className="flex gap-4 items-center">
            <Link to="/faq" className="hover:underline">
              الأسئلة الشائعة
            </Link>
            <Link to="/privacy" className="hover:underline">
              الخصوصية
            </Link>
            <Link to="/terms" className="hover:underline">
              الشروط
            </Link>
            <Link to="/cart" className="flex items-center gap-1">
              🛒 السلة ({cart.length})
            </Link>

            {loggedIn ? (
              <button
                onClick={() => {
                  localStorage.removeItem("loggedIn");
                  setLoggedIn(false);
                  window.location.reload();
                }}
                className="bg-black text-yellow-400 px-4 py-1 rounded-lg hover:bg-gray-800"
              >
                تسجيل الخروج
              </button>
            ) : (
              <Link
                to="/login"
                className="bg-black text-yellow-400 px-4 py-1 rounded-lg hover:bg-gray-800"
              >
                تسجيل الدخول
              </Link>
            )}
          </nav>
        </header>

        {/* ✅ الصفحات */}
        <Routes>
          <Route
            path="/"
            element={
              <HomePage />
            }
          />
          <Route path="/genshin" element={<Genshin cart={cart} setCart={setCart} />} />
          <Route path="/fifa" element={<Fifa cart={cart} setCart={setCart} />} />
          <Route path="/fortnite" element={<Fortnite cart={cart} setCart={setCart} />} />
          <Route path="/roblox" element={<Roblox cart={cart} setCart={setCart} />} />
          <Route path="/playstation" element={<Playstation cart={cart} setCart={setCart} />} />
          <Route path="/overwatch" element={<Overwatch cart={cart} setCart={setCart} />} />
          <Route path="/cart" element={<Cart cart={cart} setCart={setCart} />} />
          <Route path="/privacy" element={<PrivacyPolicy />} />
          <Route path="/terms" element={<TermsConditions />} />
          <Route path="/faq" element={<FAQ />} />
          <Route path="/login" element={<Login setLoggedIn={setLoggedIn} />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

function HomePage() {
  return (
    <div className="text-center py-10">
      <h1 className="text-3xl font-bold text-yellow-400 mb-2">
        مرحبًا بك في AMH STORE 💎
      </h1>
      <p>وجهتك الأولى لشراء الحسابات المضمونة بأفضل الأسعار.</p>
      <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-6 max-w-4xl mx-auto">
        <Link to="/genshin" className="bg-purple-700 p-6 rounded-lg hover:opacity-80">
          حسابات قنشن
        </Link>
        <Link to="/fifa" className="bg-green-600 p-6 rounded-lg hover:opacity-80">
          حسابات فيفا
        </Link>
        <Link to="/fortnite" className="bg-blue-600 p-6 rounded-lg hover:opacity-80">
          حسابات فورتنايت
        </Link>
        <Link to="/roblox" className="bg-red-600 p-6 rounded-lg hover:opacity-80">
          حسابات روبلوكس
        </Link>
        <Link to="/overwatch" className="bg-orange-600 p-6 rounded-lg hover:opacity-80">
          حسابات أوفر واتش
        </Link>
        <Link to="/playstation" className="bg-gray-700 p-6 rounded-lg hover:opacity-80">
          حسابات سوني
        </Link>
      </div>
    </div>
  );
}

export default App;
