import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";

// üü¢ ÿßŸÑÿµŸÅÿ≠ÿßÿ™ (Pages)
import Home from "./pages/Home";
import Fifa from "./pages/Fifa";
import Fortnite from "./pages/Fortnite";
import Genshin from "./pages/Genshin";
import Roblox from "./pages/Roblox";
import Playstation from "./pages/Playstation";
import Overwatch from "./pages/Overwatch";
import Cart from "./pages/Cart";
import Login from "./pages/Login";
import PrivacyPolicy from "./pages/PrivacyPolicy";
import TermsConditions from "./pages/TermsConditions";
import FAQ from "./pages/FAQ";

// üü¢ ÿßŸÑŸÖŸÉŸàŸÜÿßÿ™ (Components)
import Header from "./components/Header";

function App() {
  // üõí ÿßŸÑÿ≥ŸÑÿ©
  const [cart, setCart] = useState(() => {
    const saved = localStorage.getItem("cart");
    return saved ? JSON.parse(saved) : [];
  });

  // üîê ÿ≠ÿßŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
  const [loggedIn, setLoggedIn] = useState(() => 
    localStorage.getItem("loggedIn") === "true"
  );

  // üß† ÿ≠ŸÅÿ∏ ÿßŸÑÿ≥ŸÑÿ© ŸÅŸä ÿßŸÑÿ™ÿÆÿ≤ŸäŸÜ ÿßŸÑŸÖÿ≠ŸÑŸä ÿπŸÜÿØ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  // üíæ ÿ≠ŸÅÿ∏ ÿ≠ÿßŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÅŸä ÿßŸÑÿ™ÿÆÿ≤ŸäŸÜ ÿßŸÑŸÖÿ≠ŸÑŸä
  useEffect(() => {
    localStorage.setItem("loggedIn", loggedIn);
  }, [loggedIn]);

  return (
    <BrowserRouter>
      {/* üü† ÿ¥ÿ±Ÿäÿ∑ ÿßŸÑŸáŸäÿØÿ± */}
      <Header />

      {/* üñ§ ÿÆŸÑŸÅŸäÿ© ÿßŸÑŸÖŸàŸÇÿπ */}
      <div className="bg-black min-h-screen text-white">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/fifa" element={<Fifa />} />
          <Route path="/fortnite" element={<Fortnite />} />
          <Route path="/genshin" element={<Genshin />} />
          <Route path="/roblox" element={<Roblox />} />
          <Route path="/playstation" element={<Playstation />} />
          <Route path="/overwatch" element={<Overwatch />} />
          <Route path="/cart" element={<Cart cart={cart} setCart={setCart} />} />
          <Route path="/login" element={<Login setLoggedIn={setLoggedIn} />} />
          <Route path="/privacy" element={<PrivacyPolicy />} />
          <Route path="/terms" element={<TermsConditions />} />
          <Route path="/faq" element={<FAQ />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
