import React from "react";
import { Link } from "react-router-dom";

function Home() {
  const WHATSAPP_NUMBER = "966593105771";
  const WHATSAPP_MESSAGE = encodeURIComponent("مرحبًا 👋 أريد الاستفسار عن أحد الحسابات في AMH STORE 💎");

  const sections = [
    {
      name: "حسابات قنشن",
      image: "/genshin.jpg",
      path: "/genshin",
      color: "from-purple-700 to-indigo-900",
    },
    {
      name: "حسابات فورتنايت",
      image: "/fortnite.jpg",
      path: "/fortnite",
      color: "from-blue-500 to-blue-900",
    },
    {
      name: "حسابات أوفر واتش",
      image: "/overwatch.jpg",
      path: "/overwatch",
      color: "from-amber-600 to-yellow-800",
    },
    {
      name: "حسابات روبلوكس",
      image: "/roblox.jpg",
      path: "/roblox",
      color: "from-red-700 to-rose-900",
    },
    {
      name: "حسابات سوني",
      image: "/sony.jpg",
      path: "/playstation",
      color: "from-blue-800 to-gray-900",
    },
    {
      name: "حسابات فيفا",
      image: "/fifa.jpg",
      path: "/fifa",
      color: "from-green-600 to-green-900",
    },
  ];

  return (
    <div className="min-h-screen bg-black text-white">
      <div className="text-center py-10">
        <h1 className="text-3xl font-bold text-yellow-400 mb-2">
          مرحبًا بك في AMH STORE 💎
        </h1>
        <p className="text-gray-300 mb-10">
          وجهتك الأولى لشراء الحسابات المضمونة بأفضل الأسعار.
        </p>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8 px-10">
          {sections.map((item, index) => (
            <div
              key={index}
              className={`relative rounded-xl overflow-hidden shadow-lg group bg-gradient-to-b ${item.color} transition-all duration-300 hover:scale-105`}
            >
              {/* صورة الخلفية */}
              <img
                src={item.image}
                alt={item.name}
                className="absolute inset-0 w-full h-full object-cover opacity-80 group-hover:opacity-60 transition-all"
              />
              <div className="absolute inset-0 bg-gradient-to-t from-black via-transparent to-transparent"></div>

              {/* النص */}
              <div className="absolute bottom-14 w-full text-center">
                <h3 className="text-yellow-400 font-bold text-xl mb-1">
                  {item.name}
                </h3>
                <p className="text-gray-200 text-sm">اضغط لتصفح الحسابات</p>
              </div>

              {/* زر دخول القسم */}
              <div className="absolute bottom-4 w-full text-center">
                <Link
                  to={item.path}
                  className="inline-block bg-yellow-400 text-black font-bold px-4 py-1 rounded-lg hover:bg-yellow-500 transition"
                >
                  تصفح الآن
                </Link>
              </div>
            </div>
          ))}
        </div>

        {/* 🔹 زر واتساب أسفل الصفحة */}
        <div className="mt-12">
          <a
            href={`https://wa.me/${WHATSAPP_NUMBER}?text=${WHATSAPP_MESSAGE}`}
            target="_blank"
            rel="noopener noreferrer"
            className="inline-flex items-center gap-2 bg-green-500 text-white px-6 py-3 rounded-xl text-lg font-bold shadow-lg hover:bg-green-600 transition"
          >
            💬 تواصل معنا على الواتساب لشراء الحسابات
          </a>
          <p className="text-gray-400 mt-2">رقم التواصل الرسمي: +966 59 310 5771</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
