import React from "react";
import { Link } from "react-router-dom";
import fifa from "./fifa.jpg";

export default function Fifa({ setCart }) {
  const product = {
    id: 6,
    name: "حساب فيفا",
    price: 55,
    image: fifa,
    description: "حساب فيفا فيه كوينز كثيرة ولاعبين نادرين ⚽",
  };

  const addToCart = () => {
    const cart = JSON.parse(localStorage.getItem("cart")) || [];
    cart.push(product);
    localStorage.setItem("cart", JSON.stringify(cart));
    setCart(cart);
    alert("تمت إضافة الحساب إلى السلة ✅");
  };

  return (
    <div className="text-center bg-black text-white min-h-screen p-10">
      <h1 className="text-3xl font-bold text-amber-400 mb-6">{product.name}</h1>
      <img src={product.image} alt={product.name} className="w-96 mx-auto rounded-2xl mb-4" />
      <p className="text-gray-300 mb-3">{product.description}</p>
      <p className="text-lg mb-3 text-amber-300">السعر: {product.price} ريال</p>
      <button
        onClick={addToCart}
        className="bg-amber-400 text-black px-6 py-2 rounded-lg font-semibold hover:bg-amber-500"
      >
        أضف إلى السلة
      </button>
      <div className="mt-6">
        <Link to="/" className="text-blue-400 hover:text-amber-300">⬅ رجوع إلى الرئيسية</Link>
      </div>
    </div>
  );
}
