import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Login({ setLoggedIn }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = () => {
    if (email && password) {
      setLoggedIn(true);
      localStorage.setItem("loggedIn", "true");
      alert("تم تسجيل الدخول بنجاح ✅");
      navigate("/");
    } else {
      alert("يرجى إدخال البريد وكلمة المرور ❌");
    }
  };

  return (
    <div className="bg-black text-white min-h-screen flex flex-col items-center justify-center">
      <h1 className="text-3xl text-amber-400 font-bold mb-6">تسجيل الدخول</h1>
      <div className="bg-gray-900 p-6 rounded-xl w-80">
        <input
          type="email"
          placeholder="البريد الإلكتروني"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="w-full p-2 mb-4 rounded bg-gray-800 border border-amber-400"
        />
        <input
          type="password"
          placeholder="كلمة المرور"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full p-2 mb-4 rounded bg-gray-800 border border-amber-400"
        />
        <button
          onClick={handleLogin}
          className="bg-amber-400 text-black w-full py-2 rounded-lg hover:bg-amber-500"
        >
          دخول
        </button>
      </div>
    </div>
  );
}
