import React from "react";
import { WHATSAPP_NUMBER, WHATSAPP_MESSAGE } from "../config";

export default function Cart({ cart }) {
  const handleCheckout = () => {
    alert("ŸÑÿ•ÿ™ŸÖÿßŸÖ ÿßŸÑÿ∑ŸÑÿ® ÿ™ŸàÿßÿµŸÑ ŸÖÿπŸÜÿß ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ®. ÿ≥Ÿäÿ™ŸÖ ŸÅÿ™ÿ≠ ÿßŸÑÿ™ÿ∑ÿ®ŸäŸÇ ÿßŸÑÿ¢ŸÜ...");
    const text = encodeURIComponent(
      WHATSAPP_MESSAGE + "\n\nÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ≥ŸÑÿ©:\n" + cart.map(i => `- ${i.title}`).join("\n")
    );
    window.open(`https://wa.me/${WHATSAPP_NUMBER}?text=${text}`, "_blank");
  };

  return (
    <div className="text-center text-white py-10">
      <h1 className="text-3xl text-yellow-400 mb-6">üõçÔ∏è ÿ≥ŸÑÿ© ÿßŸÑŸÖÿ¥ÿ™ÿ±Ÿäÿßÿ™</h1>

      {cart.length === 0 ? (
        <p>ÿßŸÑÿ≥ŸÑÿ© ŸÅÿßÿ±ÿ∫ÿ©.</p>
      ) : (
        <>
          {cart.map((item, i) => (
            <div key={i} className="bg-gray-800 mx-auto p-4 mb-3 w-80 rounded-lg">
              <img src={item.image} alt={item.title} className="rounded mb-2" />
              <h3 className="text-yellow-300">{item.title}</h3>
              <p>ÿßŸÑÿ≥ÿπÿ±: {item.price} ÿ±ŸäÿßŸÑ</p>
            </div>
          ))}
          <button
            onClick={handleCheckout}
            className="bg-yellow-400 text-black px-5 py-2 rounded hover:bg-yellow-500"
          >
            ÿ•ÿ™ŸÖÿßŸÖ ÿßŸÑÿ∑ŸÑÿ® ÿπÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßÿ®
          </button>
        </>
      )}
    </div>
  );
}
