import React, { useState } from "react";
import overwatchAccounts from "../data/overwatch";
import GalleryModal from "../components/GalleryModal";

export default function Overwatch({ cart, setCart }) {
  const [open, setOpen] = useState(false);
  const [curr, setCurr] = useState(0);     // أي حساب (لو عندك أكثر من حساب)
  const [idx, setIdx] = useState(0);       // فهرس الصورة داخل المعرض

  const acc = overwatchAccounts[curr] ?? overwatchAccounts[0];

  const addToCart = () => {
    setCart((c) => [...c, { id: acc.id, title: acc.title, price: acc.price }]);
  };

  const whatsappLink = `https://wa.me/${acc.whatsapp}?text=${encodeURIComponent(
    `أرغب بشراء: ${acc.title} (ID: ${acc.id}) بسعر ${acc.price} ريال`
  )}`;

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <h1 className="text-3xl font-bold text-amber-300 text-center mb-6">حسابات أوفر واتش</h1>

      {/* البطاقة */}
      <div className="bg-[#0f172a] rounded-2xl shadow-xl overflow-hidden">
        <img
          src={acc.cover}
          alt={acc.title}
          className="w-full max-h-[420px] object-cover"
          onClick={() => { setIdx(0); setOpen(true); }}
        />

        <div className="p-5">
          <h2 className="text-2xl font-extrabold text-white mb-2">{acc.title}</h2>
          <p className="text-gray-300 mb-3">{acc.short}</p>

          <ul className="list-disc list-inside text-gray-300 space-y-1 mb-4">
            {acc.details.map((d, i) => <li key={i}>{d}</li>)}
          </ul>

          <div className="flex items-center gap-3 flex-wrap">
            <span className="text-emerald-400 font-bold text-lg">{acc.price} ريال</span>

            <button
              onClick={addToCart}
              className="bg-amber-400 hover:bg-amber-500 text-black font-semibold px-4 py-2 rounded-xl"
            >
              أضف إلى السلة
            </button>

            <a
              href={whatsappLink}
              target="_blank" rel="noreferrer"
              className="bg-emerald-500 hover:bg-emerald-600 text-white px-4 py-2 rounded-xl"
            >
              تواصل واتساب
            </a>

            <button
              onClick={() => { setIdx(0); setOpen(true); }}
              className="bg-white/10 hover:bg-white/20 text-white px-4 py-2 rounded-xl"
            >
              عرض التفاصيل (معرض الصور)
            </button>
          </div>

          {/* شبكة صور مصغرة سريعة تحت البطاقة */}
          <div className="grid grid-cols-2 sm:grid-cols-4 md:grid-cols-6 gap-2 mt-6">
            {acc.gallery.map((g, i) => (
              <img
                key={g}
                src={g}
                alt={`thumb-${i + 1}`}
                className="h-24 w-full object-cover rounded-lg cursor-pointer hover:opacity-80"
                onClick={() => { setIdx(i); setOpen(true); }}
                loading="lazy"
              />
            ))}
          </div>
        </div>
      </div>

      {/* مودال المعرض */}
      <GalleryModal
        open={open}
        images={acc.gallery}
        index={idx}
        onClose={() => setOpen(false)}
        onIndex={(updater) => setIdx((i) =>
          typeof updater === "function" ? updater(i) : updater
        )}
      />
    </div>
  );
}
