import React, { useState } from "react";

const Login = ({ setLoggedIn }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = (e) => {
    e.preventDefault();

    if (email.trim() === "" || password.trim() === "") {
      alert("يرجى إدخال البريد الإلكتروني وكلمة المرور");
      return;
    }

    // تسجيل الدخول التجريبي
    alert("تم تسجيل الدخول بنجاح ✅");
    setLoggedIn(true);
  };

  return (
    <div className="bg-black text-white min-h-screen flex flex-col items-center justify-center">
      <h1 className="text-3xl font-bold text-amber-400 mb-6">تسجيل الدخول</h1>

      <form
        onSubmit={handleLogin}
        className="bg-gray-900 p-8 rounded-xl shadow-lg w-80 text-right"
      >
        <label className="block mb-2 text-amber-300">البريد الإلكتروني:</label>
        <input
          type="email"
          className="w-full p-2 mb-4 rounded bg-gray-800 text-white focus:outline-none"
          placeholder="example@gmail.com"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <label className="block mb-2 text-amber-300">كلمة المرور:</label>
        <input
          type="password"
          className="w-full p-2 mb-4 rounded bg-gray-800 text-white focus:outline-none"
          placeholder="••••••••"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <button
          type="submit"
          className="bg-amber-500 hover:bg-amber-600 text-black font-bold py-2 px-4 rounded w-full"
        >
          تسجيل الدخول
        </button>
      </form>
    </div>
  );
};

export default Login;
