import React from "react";
import { Link } from "react-router-dom";

export default function PrivacyPolicy() {
  return (
    <div className="bg-black text-white min-h-screen p-10 text-center">
      <h1 className="text-3xl font-bold text-amber-400 mb-6">سياسة الخصوصية</h1>
      <p className="text-gray-300 leading-relaxed mb-4">
        نحن في <strong>AMH STORE</strong> نحترم خصوصية عملائنا، ونعمل على حماية بياناتكم وعدم مشاركتها مع أي طرف خارجي.
        جميع عمليات الشراء والدفع مؤمنة ومشفرة بشكل كامل 🔒.
      </p>
      <p className="text-gray-400 mb-6">استخدامك لموقعنا يعني موافقتك على سياسة الخصوصية هذه.</p>
      <Link to="/" className="text-blue-400 hover:text-amber-300">⬅ العودة إلى الصفحة الرئيسية</Link>
    </div>
  );
}
